import { useEffect, useState } from "react"
import SidebarGroup from "./SidebarGroup"
import { getEmployees, getProjects } from "../../api"

const Sidebar = () => {
    const [employees, setEmployees] = useState([])
    const [projects, setProjects] = useState([])
    const [error, setError] = useState(null)

    useEffect(() => {
        Promise.all([getEmployees(), getProjects()])
            .then((employeesResponse, projectsResponse) => {
                setEmployees(employeesResponse.data)
                setProjects(projectsResponse.data)
            })
            .catch((err) => {
                console.error("Error fetching data:", err)
                setError("Nie udało się pobrać danych")
            })
    }, [])

    if(error) return <div className="error">{error}</div>

    if(!employees.length) return <div>Ładowanie pracowników...</div>
    if(!projects.length) return <div>Ładowanie projektów...</div>

    return (
        <div className="sidebar">
            <h2 className="sidebar-title">WorkChrono</h2>
            <SidebarGroup
                title="Pracownicy"
                items={employees.map((employee) => `${employee.first_name} ${employee.last_name}`
                )}
            />
            <SidebarGroup title="Projekty" items={["Zadania", "Projekty", "Spotkania"]} />
        </div>
    )
}

export default Sidebar
