import { useState } from "react"
import SidebarItem from "./SidebarItem"

const SidebarGroup = ({ title, items }) => {
    const [isOpen, setIsOpen] = useState(false) // Rozwijanie/zwijanie grupy

    const toggleGroup = () => setIsOpen(!isOpen)

    return (
        <div className="sidebar-group">
            <div className="group-title" onClick={toggleGroup}>
                <span>{title}</span>
                <span>{isOpen ? "▼" : "▶"}</span>
            </div>
            {isOpen && (
                <ul className="group-items">
                    {items.map((item, index) => (
                        <SidebarItem key={index} label={item} />
                    ))}
                </ul>
            )}
        </div>
    )
}

export default SidebarGroup
