import { useEffect, useState } from "react"
import SidebarGroup from "./SidebarGroup"
import { getEmployees, getProjects } from "../../api"
import Collapse from "react-bootstrap/esm/Collapse";
import Button from "react-bootstrap/esm/Button";
import ListGroup from "react-bootstrap/ListGroup"

const Sidebar = () => {
    // const [employees, setEmployees] = useState([])
    // const [projects, setProjects] = useState([])
    // const [error, setError] = useState(null)

    // Stany kontrolujące rozwinięcie sekcji
    const [showEmployees, setShowEmployees] = useState(false);
    const [showProjects, setShowProjects] = useState(false);

    // Funkcje przełączające widoczność sekcji
    const toggleEmployees = () => setShowEmployees(!showEmployees);
    const toggleProjects = () => setShowProjects(!showProjects);

    useEffect(() => {
        Promise.all([getEmployees(), getProjects()])
            .then((employeesResponse, projectsResponse) => {
                setEmployees(employeesResponse.data)
                setProjects(projectsResponse.data)
            })
            .catch((err) => {
                console.error("Error fetching data:", err)
                setError("Nie udało się pobrać danych")
            })
    }, [])

    if(error) return <div className="error">{error}</div>

    if(!employees.length) return <div>Ładowanie pracowników...</div>
    if(!projects.length) return <div>Ładowanie projektów...</div>

    return (
        <div style={{ width: "250px", backgroundColor: "#f8f9fa", padding: "10px" }}>
            <h4 className="mb-3">WorkChrono</h4>
            <Button
                variant="light"
                onClick={toggleEmployees}
                aria-controls="employees-list"
                aria-expanded={showEmployees}
                className="w-100 text-start"
            >
                {showEmployees ? "▼ Pracownicy" : "▶ Pracownicy"}
            </Button>
            <Collapse in={showEmployees}>
                <div id="employees-list">
                    <SidebarGroup
                        title="Pracownicy"
                        // items={employees.map((employee) => `${employee.first_name} ${employee.last_name}`)}
                        items={["Zadania", "Projekty", "Spotkania"]}
                    />
                </div>
            </Collapse>

            {/* Sekcja Projekty */}
            <Button
                variant="light"
                onClick={toggleProjects}
                aria-controls="projects-list"
                aria-expanded={showProjects}
                className="w-100 text-start mt-2"
            >
                {showProjects ? "▼ Projekty" : "▶ Projekty"}
            </Button>
            <Collapse in={showProjects}>
                <div id="projects-list">
                    <ListGroup numbered>
                        <ListGroup.Item>Projekt Alpha</ListGroup.Item>
                        <ListGroup.Item>Projekt Beta</ListGroup.Item>
                        <ListGroup.Item>Projekt Gamma</ListGroup.Item>
                    </ListGroup>
                </div>
            </Collapse>
        </div>
    )
}

export default Sidebar
