import { useEffect, useState } from "react"
import { getEmployeeTasks } from "../../api"
import ListGroup from "react-bootstrap/ListGroup"
import ListGroupItem from "react-bootstrap/esm/ListGroupItem";

const TasksTab = ({ employee_id, project_id }) => {
    const [tasks, setTasks] = useState([])
    const [expandedTaskId, setExpandedTaskId] = useState(null)

    const toggleTaskDetails = (taskId) => {
        setExpandedTaskId(expandedTaskId === taskId ? null : taskId)
    }

    useEffect(() => {
        getEmployeeTasks(employee_id)
            .then((response) => {
                setTasks(response.data || [])
            })
            .catch((err) => {
                console.error("Error fetching employee tasks:", err)
                setTasks([])
            })
    }, [employee_id])

    if(!tasks.length) return <div>Ładowanie zadań pracownika...</div>

    return (
        <div>
            <h3>Zadania pracownika {employee_id}</h3>
            <ListGroup numbered>
                {tasks.map((task) => (
                    <div key={task.task_id}>
                        <ListGroupItem
                            action
                            onClick={() => toggleTaskDetails(task.task_id)}
                            style={{cursor}}
                        >
                            {task.name}
                        </ListGroupItem>
                    </div>
                ))}
            </ListGroup>
        </div>
    )
}

export default TasksTab
